import styled from 'styled-components'

export const AppContainer = styled.div`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: #25262c;
`

export const HeaderElement = styled.h1`
  font-family: Roboto;
  color: #ffffff;
  margin-top: 60px;
  margin-bottom: 60px;
`
export const ContentContainer = styled.div`
  height: 680px;
  width: 1100px;
  display: flex;
  flex-direction: row;
  align-items: center;
  background-color: #1b1c22;
  border-radius: 5px;
`
export const ImageContainer = styled.div`
  height: 680px;
  width: 50%;
  display: flex;
  flex-direction: column;
  align-items: center;
`
export const ImageElement = styled.img`
  height: 400px;
  width: 400px;
`

export const TextContainer = styled.div`
  height: 620px;
  width: 45%;
  margin-left: 25px;
  border-radius: 10px;
  background-color: #25262c;
  padding-left: 15px;
  padding-right: 15px;
`
export const ButtonContainer = styled.div`
  height: 45px;
  width: 460px;
  display: flex;
  flex-direction: row;
  align-items: center;
  border: 1px solid #272c34;
`
export const Button = styled.button`
  height: 35px;
  width: 35px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-right: 15px;
  cursor: pointer;
  background-color: transparent;
  border-color: transparent;
`
export const TextArea = styled.textarea`
  height: 300px;
  width: 300px;
`
